const WorkoutItem = ({ icon, iconActive, text, onPress, selected }) => {
  const cardStyle =
    selected === text ? workoutsStyle.cardSelected : workoutsStyle.card;
  return (
    <TouchableOpacity style={cardStyle} onPress={onPress}>
      {selected === text ? iconActive : icon}
      <Text style={workoutsStyle.cardText}>{text}</Text>
    </TouchableOpacity>
  );
};

const WorkoutsGrid = () => {
  const [isActive, setIsActive] = useState("");

  const handleActive = (item) => {
    setIsActive(item);

    if (item === isActive) {
      setIsActive("");
    }
  };

  return (
    <View>
      <View
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "center",
        }}
      >
        <WorkoutItem
          icon={<ChestIcon />}
          iconActive={<ChestIcon isActive />}
          text={"Chest"}
          onPress={() => handleActive("Chest")}
          selected={isActive}
        />
        <WorkoutItem
          icon={<ShouldersIcon />}
          iconActive={<ShouldersIcon isActive />}
          text={"Shoulders"}
          onPress={() => handleActive("Shoulders")}
          selected={isActive}
        />
        <WorkoutItem
          icon={<BackIcon />}
          iconActive={<BackIcon isActive />}
          text={"Back"}
          onPress={() => handleActive("Back")}
          selected={isActive}
        />
      </View>

      <View
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "center",
        }}
      >
        <WorkoutItem
          icon={<CardioIcon />}
          iconActive={<CardioIcon isActive />}
          text={"Cardio"}
          onPress={() => handleActive("Cardio")}
          selected={isActive}
        />
        <WorkoutItem
          icon={<LegsIcon />}
          iconActive={<LegsIcon isActive />}
          text={"Legs"}
          onPress={() => handleActive("Legs")}
          selected={isActive}
        />
        <WorkoutItem
          icon={<ArmsIcon />}
          iconActive={<ArmsIcon isActive />}
          text={"Arms"}
          onPress={() => handleActive("Arms")}
          selected={isActive}
        />
      </View>

      <View
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "center",
        }}
      >
        <WorkoutItem
          icon={<StretchingIcon />}
          iconActive={<StretchingIcon isActive />}
          text={"Stretching"}
          onPress={() => handleActive("Stretching")}
          selected={isActive}
        />
        <WorkoutItem
          icon={<AbsIcon />}
          iconActive={<AbsIcon isActive />}
          text={"Abs"}
          onPress={() => handleActive("Abs")}
          selected={isActive}
        />
      </View>
    </View>
  );
};

export default WorkoutsGrid;
